import Vue from 'vue';
import VueI18n from 'vue-i18n';
import customZhCn from "./lang/zh-CN";
import customZhTw from "./lang/zh-TW";
import customEnUs from "./lang/en-US";
import zhCnLocale from 'view-design/dist/locale/zh-CN';
import enUsLocale from 'view-design/dist/locale/en-US';
import zhTwLocale from 'view-design/dist/locale/zh-TW';
Vue.use(VueI18n); // 自动根据浏览器系统语言设置语言

var navLang = navigator.language;
var localLang = navLang === 'zh-CN' || navLang === 'en-US' ? navLang : false;
var lang = localLang || 'en-US' || 'zh-CN';
Vue.config.lang = lang; // vue-i18n 6.x+写法

Vue.locale = function () {};

var messages = {
  'zh-CN': Object.assign(zhCnLocale, customZhCn),
  'zh-TW': Object.assign(zhTwLocale, customZhTw),
  'en-US': Object.assign(enUsLocale, customEnUs)
};
var i18n = new VueI18n({
  locale: lang,
  messages: messages
});
export default i18n;
